import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { fetchResults } from './services/resultsServices';
import './ResultsPage.css';

const ResultsPage = () => {
  const [results, setResults] = useState({
    videos: [],
    courses: [],
    reports: [],
    books: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();

  useEffect(() => {
    const fetchData = async () => {
      const params = new URLSearchParams(location.search);
      const topic = params.get('topic');
      
      try {
        const data = await fetchResults(topic);
        setResults(data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch results. Please try again.');
        setLoading(false);
      }
    };

    fetchData();
  }, [location]);

  if (loading) return <div className="loading">Loading results...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="results-page">
      <h1>Results for "{new URLSearchParams(location.search).get('topic')}"</h1>
      
      <div className="results-container">
        {Object.entries(results).map(([category, items]) => (
          <div key={category} className="result-column">
            <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
            <div className="result-tiles">
              {items.map((item, index) => (
                <a key={index} href={item.link} target="_blank" rel="noopener noreferrer" className="result-tile">
                  <h3>{item.title}</h3>
                  <p>{item.description}</p>
                </a>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ResultsPage;
